# 695. Max Area of Island
from typing import List


class _695_Solution:
    def maxAreaOfIsland(self, grid: List[List[int]]) -> int:
        if not grid or not grid[0]: return 0
        r, c = len(grid), len(grid[0])
        visited, ans = set(), 0
        offset = [(-1, 0), (1, 0), (0, -1), (0, 1)]

        def dfs(x, y):
            if x < 0 or x >= r or y < 0 or y >= c or (x, y) in visited or grid[x][y] == 0:
                return 0
            visited.add((x, y))
            return 1 + sum(map(lambda lst: dfs(x + lst[0], y + lst[1]), offset))

        for i in range(r):
            for j in range(c):
                if grid[i][j] == 0 or (i, j) in visited: continue
                ans = max(ans, dfs(i, j))
        return ans


